import Layout from '../components/layout.js';
import {useState} from 'react';

const IndexPage = () => {
  const [name, setName] = useState('');

  const doChatShit = () => {
    alert(`Welcome ${name}`);
    setName('');
  };

  return (
    <Layout>
      <h1>Welcome to Web-Chat</h1>
      <input
        placeholder="your name ?"
        onChange={e => setName(e.target.value)}
        onKeyPress={e => {
          e.which === 13 ? doChatShit() : null;
        }}
        value={name}
        style={{
          width: 130,
          padding: '8px',
          margin: '10px',
          fontSize: 16,
          borderRadius: 2,
          border: '1px solid #DDD',
        }}
      />
      <br />
      <button onClick={doChatShit}>Enter</button>
      <style jsx>{`
        button {
          padding: 8px;
          margin: 0 10px;
          font-size: 14px;
          font-style: bold;
          background-color: #4285f4;
          border-radius: 3px;
          color: white;
          border: none;
        }
      `}</style>
    </Layout>
  );
};

export default IndexPage;
