import Layout from '../components/layout.js';
import {useState, useEffect} from 'react';
import RTCinit from '../RTCinit.js';

const IndexPage = () => {
  const [name, setName] = useState('');
  const [showbox, setShowbox] = useState(true);

  const doChatShit = () => {
    alert(`Welcome ${name}`);
    setShowbox(false);
  };

  useEffect(() => {
    console.log(location);
    RTCinit();
  });

  return (
    <Layout>
      <h1>Welcome to Web-Chat{!showbox && `, ${name}`}</h1>
      {showbox && (
        <div className="welcome-box">
          <input
            placeholder="your name ?"
            onChange={e => setName(e.target.value)}
            onKeyPress={e => {
              e.which === 13 ? doChatShit() : null;
            }}
            value={name}
            style={{
              width: 130,
              padding: '8px',
              margin: '10px',
              fontSize: 16,
              borderRadius: 2,
              border: '1px solid #DDD',
            }}
          />
          <br />
          <button onClick={doChatShit}>Enter</button>
        </div>
      )}
      <div className="content">
        <div className="messages" />
        <form className="footer" onsubmit="return false;">
          <input type="text" placeholder="Your message.." />
          <button type="submit">Send</button>
        </form>
      </div>

      <template data-template="message">
        <div className="message">
          <div className="message__name" />
          <div className="message__bubble" />
        </div>
      </template>
      <style jsx>{`
        h1 {
          font-family: Arial;
        }
        button {
          padding: 8px;
          margin: 0 10px;
          font-size: 15px;
          font-style: bold;
          background-color: #4285f4;
          border-radius: 3px;
          color: white;
          border: none;
        }
      `}</style>
    </Layout>
  );
};

export default IndexPage;
