import Layout from '../components/layout.js';
import {useState} from 'react';

const IndexPage = () => {
  const [name, setName] = useState('');

  return (
    <Layout>
      <h1>Welcome to Web-Chat</h1>
      <input
        placeholder="your name ?"
        onChange={e => setName(e.target.value)}
        onKeyPress={e => {
          e.keyCode === 13 ? alert(`Welcome ${name}`) : null;
        }}
        value={name}
        style={{
          width: 130,
          padding: '8px',
          margin: '10px',
        }}
      />
      <br />
      <button
        onClick={() => {
          alert(`Welcome ${name}`);
          setName('');
        }}>
        Enter
      </button>
      <style jsx>{`
        button {
          padding: 8px;
          margin: 0 10px;
          background-color: forestgreen;
          color: white;
          border: none;
        }
      `}</style>
    </Layout>
  );
};

export default IndexPage;
